// script.js

// --- Get elements from the HTML ---
const timerDisplay = document.getElementById('timer-display');
const startTimeDisplay = document.getElementById('start-time-display');
const eggContainer = document.getElementById('egg-container');
const timerButtons = document.querySelectorAll('.timer-button');
const resetButton = document.getElementById('reset-button');

// --- Timer variables ---
let countdown;
let timeRemaining;
let isAudioUnlocked = false; // A flag to check if we've unlocked audio yet

// --- Define audio using Base64 strings. No more external files! ---
const alarmSound = new Audio('data:audio/mp3;base64,-2');
const boilingSound = new Audio('data:audio/mp3;base64,---------------Q==');

boilingSound.loop = true;

/**
 * This function is called ONLY ONCE on the first click.
 * It plays and immediately pauses both sounds. This is a necessary
 * trick to get around strict browser autoplay policies, especially on mobile.
 */
function unlockAudio() {
    if (isAudioUnlocked) return; // Don't run this more than once
    
    alarmSound.play().then(() => alarmSound.pause()).catch(() => {});
    boilingSound.play().then(() => boilingSound.pause()).catch(() => {});

    isAudioUnlocked = true;
}

function startTimer(seconds) {
    // 1. Clear any existing timers and stop sounds
    clearInterval(countdown);
    stopSounds();

    // 2. Start the boiling sound
    boilingSound.play();

    // 3. Set the time and start the "boiling" animation
    timeRemaining = seconds;
    eggContainer.classList.add('boiling');

    // 4. Display the start time
    const now = new Date();
    const startTimeText = `Boiling started at ${now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;
    startTimeDisplay.textContent = startTimeText;

    // 5. Set up the interval to run every second
    countdown = setInterval(() => {
        timeRemaining--;
        displayTimeLeft(timeRemaining);

        if (timeRemaining <= 0) {
            finishTimer();
        }
    }, 1000);
    
    // 6. Display the time immediately
    displayTimeLeft(timeRemaining);
}

function displayTimeLeft(seconds) {
    const minutes = Math.floor(seconds / 60);
    const remainderSeconds = seconds % 60;
    const display = `${String(minutes).padStart(2, '0')}:${String(remainderSeconds).padStart(2, '0')}`;
    timerDisplay.textContent = display;
    document.title = `${display} - Egg Timer`;
}

function finishTimer() {
    clearInterval(countdown);
    stopSounds();
    alarmSound.play(); // Play the final alarm
    eggContainer.classList.remove('boiling');
    timerDisplay.textContent = "Done!";
    document.title = "Your egg is ready!";
}

function resetTimer() {
    clearInterval(countdown);
    stopSounds();
    eggContainer.classList.remove('boiling');
    timerDisplay.textContent = "00:00";
    startTimeDisplay.textContent = ""; // FIX: Removed the stray underscore
    document.title = "The Perfect Egg Timer";
}

function stopSounds() {
    boilingSound.pause();
    boilingSound.currentTime = 0;
    alarmSound.pause();
    alarmSound.currentTime = 0;
}

// --- Event Listeners ---
timerButtons.forEach(button => {
    button.addEventListener('click', () => {
        // On the very first click, we run the unlock function.
        unlockAudio();

        const time = parseInt(button.dataset.time);
        startTimer(time);
    });
});

resetButton.addEventListener('click', resetTimer);
